import React, { Component } from "react";
import "./App.css";
import { Route } from "react-router";
import { NavLink } from "react-router-dom";
import NoteList from "./components/NoteList";
import NoteDetail from "./components/NoteDetail";
import NoteForm from "./components/NoteForm";
import axios from "axios";
import tag from './components/tag.ico';

class App extends Component {
  constructor() {
    super();
    this.state = {
      notes: [],
      tags: [],
      tag: '',
      title: "",
      contents: "",
      _id: ""
    };
  }

  fetchNotes = () => {
    axios
      .get("http://localhost:4000/api/posts")
      .then(response => {
        this.setState({ notes: response.data });
      })
      .catch(err => console.log(err));
  };

  fetchTags = () => {
    axios
      .get("http://localhost:4000/api/tags")
      .then(response => {
        this.setState({ tags: response.data });
      })
      .catch(err => console.log(err));
  };

  componentDidMount() {
    this.fetchNotes();
    this.fetchTags();
  }

  handleInput = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmitTag = e => {
    e.preventDefault();
    this.setState({
      tags: [...this.state.tags, this.state.tag],
      tag: ''
    })
  }

  postNotes = note => {
    axios
      .post("http://localhost:4000/api/posts", note)
      .then(response => {
        this.setState({
          tag: "",
          title: "",
          contents: "",
        });
        this.fetchNotes();
      })
      .catch(err => console.log(err));
  };

  deleteTag = id => {
    axios
      .delete(`http://localhost:4000/api/tags/${id}`)
      .then(response => {
        console.log(id);
        this.fetchTags();
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div className="App">
        <header className="header">
          <NavLink
            to="/"
            activeClassName="activeNewNote"
            style={{ textDecoration: "none", color: "inherit" }}
          >
            <h1>Lambda Notes</h1>
          </NavLink>
          <NavLink
            to="/new-note"
            activeClassName="activeNewNote"
            style={{ textDecoration: "none", color: "inherit" }}
          >
            <h2>+ New Note</h2>
          </NavLink>
          <div className="tags">
            <div className="tag-header">
              <img src={tag} alt="tag-icon" className="tag-icon" />
              <h2>Tags</h2>
            </div>
            {this.state.tags.map(tag => {
              return <div className="tag" key={tag.id}>
                <p>{tag.tag}{tag.id}</p>
                <p className='x hide' onClick={this.deleteTag()}>|  x</p>
              </div>
            })}
          </div>
        </header>
        <div className="body">
          <Route
            path="/new-note"
            render={props => (
              <NoteForm
                {...props}
                notes={this.state.notes}
                tags={this.state.tags}
                tag={this.state.tag}
                title={this.state.title}
                contents={this.state.contents}
                _id={this.state._id}
                handleInput={this.handleInput}
                handleSubmitTag={this.handleSubmitTag}
                postNotes={this.postNotes}
              />
            )}
          />
          <Route
            path="/"
            exact
            component={() => <NoteList notes={this.state.notes} />}
          />
          <Route
            path="/notes/:id"
            render={props => (
              <NoteDetail
                {...props}
                notes={this.state.notes}
                fetchNotes={this.fetchNotes}
                handleInput={this.handleInput}
                handleSubmitTag={this.handleSubmitTag}
              />
            )}
          />
        </div>
      </div>
    );
  }
}

export default App;
